package com.nong.nongo2o.entity.domain;import com.nong.nongo2o.entity.base.PagingEntity;import com.nong.nongo2o.entity.bean.SimpleUser;/** * <p>Description: 实体类Entity <p> * <p>Module:	 <p> * * * @author wisdom * @date: 2017-07-18 21:20:27 * @since V1.0 */public class Bill extends PagingEntity {	/**	 * 账单编码	 */	private String billCode;	private String billName;	/**	* 用户编码	*/	private SimpleUser user;	private String userCode;	/**	* 类型 0：收入；1：支出	*/	private Integer billType;	/**	* 业务单编码 订单编码；支出时是提现单编码	*/	private String businessCode;	/**	* 金额	*/	private java.math.BigDecimal billMoney;	public String getBillName() {		return billName;	}	public void setBillName(String billName) {		this.billName = billName;	}	public SimpleUser getUser() {		return user;	}	public void setUser(SimpleUser user) {		this.user = user;	}	public String getBillCode() {		return this.billCode;	}	public void setBillCode(String billCode) {		this.billCode=billCode;	}	public String getUserCode() {	    return this.userCode;	}	public void setUserCode(String userCode) {	    this.userCode=userCode;	}	public Integer getBillType() {	    return this.billType;	}	public void setBillType(Integer billType) {	    this.billType=billType;	}	public String getBusinessCode() {	    return this.businessCode;	}	public void setBusinessCode(String businessCode) {	    this.businessCode=businessCode;	}	public java.math.BigDecimal getBillMoney() {	    return this.billMoney;	}	public void setBillMoney(java.math.BigDecimal billMoney) {	    this.billMoney=billMoney;	}}