package com.nong.nongo2o.entity.domain;import com.nong.nongo2o.entity.base.PagingEntity;import com.nong.nongo2o.entity.bean.SimpleUser;import java.math.BigDecimal;/** * <p>Description: 实体类Entity <p> * <p>Module:	 <p> * * * @author wisdom * @date: 2017-07-18 21:20:27 * @since V1.0 */public class Bill extends PagingEntity {	/**	 * 账单编码	 */	private String billCode;	private String billName;	/**	* 用户编码	*/	private SimpleUser user;	private String userCode;	/**	* 类型 0：收入；1：支出	*/	private int billType;    private int billStatus;	/**	* 业务单编码 订单编码；支出时是提现单编码	*/	private String businessCode;	private Order businessObject;	/**	* 金额	*/	private java.math.BigDecimal billMoney;    public String getBillCode() {        return billCode;    }    public void setBillCode(String billCode) {        this.billCode = billCode;    }    public String getBillName() {        return billName;    }    public void setBillName(String billName) {        this.billName = billName;    }    public SimpleUser getUser() {        return user;    }    public void setUser(SimpleUser user) {        this.user = user;    }    public String getUserCode() {        return userCode;    }    public void setUserCode(String userCode) {        this.userCode = userCode;    }    public int getBillType() {        return billType;    }    public void setBillType(int billType) {        this.billType = billType;    }    public int getBillStatus() {        return billStatus;    }    public void setBillStatus(int billStatus) {        this.billStatus = billStatus;    }    public String getBusinessCode() {        return businessCode;    }    public void setBusinessCode(String businessCode) {        this.businessCode = businessCode;    }    public Order getBusinessObject() {        return businessObject;    }    public void setBusinessObject(Order businessObject) {        this.businessObject = businessObject;    }    public BigDecimal getBillMoney() {        return billMoney;    }    public void setBillMoney(BigDecimal billMoney) {        this.billMoney = billMoney;    }}