package com.nong.nongo2o.entity.domain;import com.nong.nongo2o.entity.base.PagingEntity;import com.nong.nongo2o.entity.bean.SimpleUser;/** * <p>Description: 实体类Entity <p> * <p>Module:	 <p> * * * @author wisdom * @date: 2017-07-18 21:20:28 * @since V1.0 */public class GoodsComment extends PagingEntity {			/**	* 用户编码（买家）	*/	private SimpleUser user;	private String userCode;	/**	 * 商品所属商户的编码	 */	private SimpleUser sale;	private String saleUserCode;	/**	* 商品编码	*/	private Goods goods;	private String goodsCode;	/**	* 评论内容	*/	private String content;	/**	* 满意度	*/	private Integer star;	public SimpleUser getUser() {		return user;	}	public void setUser(SimpleUser user) {		this.user = user;	}	public SimpleUser getSale() {		return sale;	}	public void setSale(SimpleUser sale) {		this.sale = sale;	}	public String getUserCode() {		return userCode;	}	public void setUserCode(String userCode) {		this.userCode = userCode;	}	public String getSaleUserCode() {		return saleUserCode;	}	public void setSaleUserCode(String saleUserCode) {		this.saleUserCode = saleUserCode;	}	public Goods getGoods() {		return goods;	}	public void setGoods(Goods goods) {		this.goods = goods;	}	public String getGoodsCode() {		return goodsCode;	}	public void setGoodsCode(String goodsCode) {		this.goodsCode = goodsCode;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}	public Integer getStar() {		return star;	}	public void setStar(Integer star) {		this.star = star;	}}