package com.nong.nongo2o.entity.domain;import com.nong.nongo2o.entity.base.PagingEntity;/** * <p>Description: 实体类Entity <p> * <p>Module:	 <p> * * * @author wisdom * @date: 2017-07-31 15:56:02 * @since V1.0 */public class GoodsSpec extends PagingEntity {			/**	* 规格编码	*/	private String specCode;	/**	* 商品编码	*/	private String goodsCode;	/**	* 规格名称	*/	private String title;	/**	* 规格价格	*/	private java.math.BigDecimal price;	/**	* 规格库存	*/	private Integer quantity;	/**	* 规格描述	*/	private String detail;	public String getSpecCode() {	    return this.specCode;	}	public void setSpecCode(String specCode) {	    this.specCode=specCode;	}	public String getGoodsCode() {	    return this.goodsCode;	}	public void setGoodsCode(String goodsCode) {	    this.goodsCode=goodsCode;	}	public String getTitle() {	    return this.title;	}	public void setTitle(String title) {	    this.title=title;	}	public java.math.BigDecimal getPrice() {	    return this.price;	}	public void setPrice(java.math.BigDecimal price) {	    this.price=price;	}	public Integer getQuantity() {	    return this.quantity;	}	public void setQuantity(Integer quantity) {	    this.quantity=quantity;	}	public String getDetail() {	    return this.detail;	}	public void setDetail(String detail) {	    this.detail=detail;	}}