package com.nong.nongo2o.entity.domain;import com.nong.nongo2o.entity.base.PagingEntity;import com.nong.nongo2o.entity.bean.SimpleUser;/** * <p>Description: 实体类Entity <p> * <p>Module:	 <p> * * * @author wisdom * @date: 2017-07-18 21:20:28 * @since V1.0 */public class MomentComment extends PagingEntity {			/**	* 动态编码	*/	private String momentCode;	/**	* 用户编码	*/	private SimpleUser user;	private String userCode;	/**	* 回复对应用户编码	*/	private SimpleUser toUser;	private String toUserCode;	/**	* 内容	*/	private String content;	public String getMomentCode() {	    return this.momentCode;	}	public void setMomentCode(String momentCode) {	    this.momentCode=momentCode;	}	public String getUserCode() {	    return this.userCode;	}	public void setUserCode(String userCode) {	    this.userCode=userCode;	}	public String getToUserCode() {	    return this.toUserCode;	}	public void setToUserCode(String toUserCode) {	    this.toUserCode=toUserCode;	}	public String getContent() {	    return this.content;	}	public void setContent(String content) {	    this.content=content;	}	public SimpleUser getUser() {		return user;	}	public void setUser(SimpleUser user) {		this.user = user;	}	public SimpleUser getToUser() {		return toUser;	}	public void setToUser(SimpleUser toUser) {		this.toUser = toUser;	}}