package com.nong.nongo2o.entity.domain;import com.nong.nongo2o.entity.base.PagingEntity;import com.nong.nongo2o.entity.bean.SimpleUser;import java.util.List;/** * <p>Description: 实体类Entity <p> * <p>Module:	 <p> * * * @author wisdom * @date: 2017-07-18 21:20:29 * @since V1.0 */public class Payment extends PagingEntity {			/**	* 支付单编码	*/	private String paymentCode;	/**	* 用户编码	*/	private SimpleUser user;	private String userCode;	/**	 * 订单编码	 */	private Order order;	private String OrderCode;	/**	* 支付状态 0：待支付；1：支付成功；2：取消支付	*/	private Integer paymentStatus;	/**	* 支付方式	*/	private Integer paymentType;	/**	* 支付金额	*/	private java.math.BigDecimal paymentPrice;	/**	* 第三支付信息	*/	private String paymentInfo;	private List<PaymentDetail> paymentDetails;	public SimpleUser getUser() {		return user;	}	public void setUser(SimpleUser user) {		this.user = user;	}	public String getPaymentCode() {	    return this.paymentCode;	}	public void setPaymentCode(String paymentCode) {	    this.paymentCode=paymentCode;	}	public String getUserCode() {	    return this.userCode;	}	public void setUserCode(String userCode) {	    this.userCode=userCode;	}	public Order getOrder() {		return order;	}	public void setOrder(Order order) {		this.order = order;	}	public String getOrderCode() {		return OrderCode;	}	public void setOrderCode(String orderCode) {		OrderCode = orderCode;	}	public Integer getPaymentStatus() {	    return this.paymentStatus;	}	public void setPaymentStatus(Integer paymentStatus) {	    this.paymentStatus=paymentStatus;	}	public Integer getPaymentType() {	    return this.paymentType;	}	public void setPaymentType(Integer paymentType) {	    this.paymentType=paymentType;	}	public java.math.BigDecimal getPaymentPrice() {	    return this.paymentPrice;	}	public void setPaymentPrice(java.math.BigDecimal paymentPrice) {	    this.paymentPrice=paymentPrice;	}	public String getPaymentInfo() {	    return this.paymentInfo;	}	public void setPaymentInfo(String paymentInfo) {	    this.paymentInfo=paymentInfo;	}	public List<PaymentDetail> getPaymentDetails() {		return paymentDetails;	}	public void setPaymentDetails(List<PaymentDetail> paymentDetails) {		this.paymentDetails = paymentDetails;	}}